module.exports = function(cardContext) {
  return [
    {
      path: '/contextual-favorite-toy/:toy',
      query: {
        filter: {
          type: { exact: 'puppies' },
          'dog-breed': cardContext.data.attributes['dog-breed'],
          'favorite-toy': ':toy'
        }
      }
    },
    {
      path: '/sorted?sort=:sort',
      query: {
        filter: {
          type: { exact: 'puppies' },
        },
        sort: ':sort'
      }
    },
  ];
};