module.exports = function (cardContext) {
  return [
    {
      path: '/buddy/:friendly_id',
      query: {
        filter: {
          type: 'puppies',
          name: { exact: ':friendly_id' }
        }
      }
    },
    {
      path: '/:type/:id',
      query: {
        filter: {
          type: ':type',
          id: { exact: ':id' }
        }
      }
    },
    {
      path: '/',
      query: {
        filter: {
          type: cardContext.data.type,
          id: { exact: cardContext.data.id }
        }
      }
    }
  ];
};