module.exports = function(cardContext) {
  return [
    {
      path: '/favorite-puppy',
      query: {
        filter: {
          type: { exact: 'puppies' },
          id: { exact: 'vanGogh' }
        }
      }
    },
    {
      path: '/buddy/:friendly_id',
      query: {
        filter: {
          type: { exact: 'puppies' },
          name: { exact: ':friendly_id' }
        }
      }
    },
    {
      path: '/contextual-favorite-toy/:toy',
      query: {
        filter: {
          type: { exact: 'puppies' },
          'dog-breed': cardContext.data.attributes['dog-breed'],
          'favorite-toy': ':toy'
        }
      }
    },
    {
      path: '/sorted?sort=:sort&extra=:extra',
      query: {
        filter: {
          type: { exact: 'puppies' },
        },
        sort: ':sort'
      }
    },
    {
      path: '/forward/:type/:id',
      query: {
        filter: {
          type: { exact: ':type' },
          id: { exact: ':id' }
        }
      }
    }
  ];
};